import React from 'react';
import './ProductPage.css';

function ProductPage({ translations }) {
  // JavaScript to handle navigation clicks and show/hide content
  const handleNavClick = (event, targetId) => {
    event.preventDefault();

    const contentSections = document.querySelectorAll('.content');
    
    // Hide all content sections
    contentSections.forEach(section => {
      section.classList.remove('active');
    });

    // Show the selected content section
    const targetSection = document.getElementById(targetId);
    targetSection.classList.add('active');
  };

  return (
    <div className="product-page">
      <header className="page-header">
        <h1>{translations.product}</h1>
      </header>
      <hr />
      <nav className="product-navbar">
        <ul>
          <li><a href="#" onClick={(e) => handleNavClick(e, 'all')}>{translations.all}</a></li>
          <li><a href="#" onClick={(e) => handleNavClick(e, 'education')}>{translations.education}</a></li>
          <li><a href="#" onClick={(e) => handleNavClick(e, 'healthcare')}>{translations.healthcare}</a></li>
          <li><a href="#" onClick={(e) => handleNavClick(e, 'it')}>{translations.it}</a></li>
          <li><a href="#" onClick={(e) => handleNavClick(e, 'general')}>{translations.general}</a></li>
        </ul>
      </nav>
      
      <div id="all" className="content">
        <h2>All Content</h2>
        <p>This is the content for "All".</p>
      </div>

      <div id="education" className="content">
        <h2>Education Content</h2>
        <p>This is the content for "Education".</p>
      </div>

      <div id="healthcare" className="content">
        <h2>Healthcare Content</h2>
        <p>This is the content for "Healthcare".</p>
      </div>

      <div id="it" className="content">
        <h2>IT Content</h2>
        <p>This is the content for "IT".</p>
      </div>

      <div id="general" className="content">
        <h2>General Content</h2>
        <p>This is the content for "General".</p>
      </div>
    </div>
  );
}

export default ProductPage;
