import React, { useState } from 'react';
import './ProductPage.css';
function ProductPage({ translations , handleCategoryClick}) {
  return (
    <div className="product-page">
    <header className="page-header">
      <h1>{translations.product}</h1>
    </header>
    <hr />
    <nav className="product-navbar">
      <ul>
        <li><a href="#" onClick={() => handleCategoryClick('all')}>{translations.all}</a></li>
        <li><a href="#" onClick={() => handleCategoryClick('education')}>{translations.education}</a></li>
        <li><a href="#" onClick={() => handleCategoryClick('healthcare')}>{translations.healthcare}</a></li>
        <li><a href="#" onClick={() => handleCategoryClick('it')}>{translations.it}</a></li>
        <li><a href="#" onClick={() => handleCategoryClick('general')}>{translations.general}</a></li>
      </ul>
    </nav>
    {selectedCategory === 'all' && (
      <>
        {renderCategoryContent('Comprehensive eSchool Management Platform', 'Education content...')}
        {renderCategoryContent('Doctors Management', 'Healthcare content...')}
        {renderCategoryContent('Call Center Solution for Telecom, Banks, and Utility Companies', 'General content...')}
        {renderCategoryContent('ITIL V3 Based Service Desk Solution', 'IT content...')}
      </>
    )}
    {selectedCategory === 'education' && renderCategoryContent('Comprehensive eSchool Management Platform', 'Education content...')}
    {selectedCategory === 'healthcare' && renderCategoryContent('Doctors Management', 'Healthcare content...')}
    {selectedCategory === 'general' && renderCategoryContent('Call Center Solution for Telecom, Banks, and Utility Companies', 'General content...')}
    {selectedCategory === 'it' && renderCategoryContent('ITIL V3 Based Service Desk Solution', 'IT content...')}
  </div>
  );
}

export default ProductPage;
